where.trim:
      and t.delflag = 0

		 and t.id =#{id}

		 and t.orgids = #{orgids}

		 and t.approvenum = #{approvenum}

		and t.approvenum like CONCAT("%",#{approvenumLike},"%")

		 and t.approvefileid = #{approvefileid}

		 and t.filename = #{filename}

		 and t.keyword = #{keyword}

		 and t.approvedate = #{approvedate}

		 and t.filesource = #{filesource}

		 and t.thirddocid = #{thirddocid}

		 and t.filerelated = #{filerelated}

		 and t.filetype = #{filetype}

		 and t.createuser = #{createuser}

		 and t.createtime = #{createtime}

		 and t.updateuser = #{updateuser}

		 and t.updatetime = #{updatetime}

		 and t.delflag = #{delflag}


		 and t2.name = #{name}

		 and t2.fileid = #{fileid}

		 and t2.dirid = #{dirid}

		 and t2.filesuffix = #{filesuffix}

		 and t2.size = #{size}

		 and t2.expiredate = #{expiredate}

		 and t2.downnum = #{downnum}

		 and t3.url = #{url}


		and t.approvedate <= #{opttimeLt} 

		and t.approvedate >= #{opttimeGt}	


	    	and t.filename like CONCAT('%',#{filenameLike},'%') 



	    	AND (


	    		t.keyword LIKE CONCAT('%',#{item},'%') 

	    	OR
	   		EXISTS (SELECT KEYWORD FROM base_file_standbook t4 WHERE t.approvefileid = t4.fileid 


	    		t4.keyword LIKE CONCAT('%',#{item},'%') 

	   		))






				 and CONCAT(";",#{orgidLike},";") like CONCAT("%;",t4.id,";%") 




				 and CONCAT(";",t.orgids,";") like CONCAT("%;",#{orgidLike},";%") 







		    AND 

			!
		    ((t.FILETYPE = 1 AND  
			(EXISTS (SELECT t5.ID FROM base_file_standbook t5 
				where t5.UPLOADTEMPID = t.ID AND t5.ISCOMPLETE != 1 
					AND CONCAT(";",t.ORGIDS,";") like CONCAT("%;",t5.ORGID,";%"))
			 OR (t.THIRDDOCID IS NOT NULL AND (t.ORGIDS IS NULL OR TRIM(t.ORGIDS) = ""))
			)) OR t.FILETYPE IS NULL)




			AND EXISTS(SELECT t6.ID FROM base_file_standbook t6 where t6.UPLOADTEMPID = t.ID 


				CONCAT("%,",t6.SUBJECT,",%") LIKE CONCAT("%,",${item},",%")

			)
